PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#>
SELECT DISTINCT ?subclass ?position (afn:localname(?position) AS ?positionName) ?positionTitle ?person ?personName ?dateTimeStart ?dateTimeEnd WHERE { GRAPH <urn:x-arq:UnionGraph> { ?subject ?property ?position . ?position a ?subclass . ?subclass rdfs:subClassOf core:Position . { { ?subject ?property ?position . ?position core:positionForPerson ?person . ?person rdfs:label ?personName } UNION { ?subject ?property ?position . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:positionForPerson ?person . ?person rdfs:label ?personName . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?position core:positionForPerson ?person . ?person rdfs:label ?personName . } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?subject ?property ?position . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?position core:positionForPerson ?person . ?person rdfs:label ?personName . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?position core:positionForPerson ?person . ?person rdfs:label ?personName } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?position core:positionForPerson ?person . ?person rdfs:label ?personName . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . ?position core:positionForPerson ?person . ?person rdfs:label ?personName } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . ?position rdfs:label ?positionTitle } UNION { ?subject ?property ?position . ?position core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . ?position core:positionForPerson ?person . ?person rdfs:label ?personName . ?position rdfs:label ?positionTitle } }
# Current positions only: end date is either null or not in the past FILTER ( !bound(?dateTimeEnd) || afn:substring(str(?dateTimeEnd), 0, 4) >= afn:substring(str(afn:now()), 0, 4) )
} } ORDER BY ?subclass ?personName
